$videoControllers = Get-WmiObject Win32_VideoController | Select-Object @{Name="ID"; Expression={$_.DeviceID}}, @{Name="Nome"; Expression={$_.Name}}, @{Name="Memória Dedicada (GB)"; Expression={if ($_.AdapterRAM) { [math]::Round($_.AdapterRAM / 1GB, 2) } else { "Indefinido" }}}, @{Name="DAC do Adaptador"; Expression={$_.AdapterDACType}}, @{Name="Disponibilidade"; Expression={ switch ($_.Availability) { 1 { "Outro" } 2 { "Desconhecido" } 3 { "Em Execução (Potência Total)" } 4 { "Aviso" } 5 { "Em Teste" } 6 { "Não Aplicável" } 7 { "Desligado" } 8 { "Offline" } 9 { "Fora de Serviço" } 10 { "Degradado" } 11 { "Não Instalado" } 12 { "Erro de Instalação" } 13 { "Recurso de Energia Desconhecido" } 14 { "Baixa Potência" } 15 { "Potência em Repouso" } 16 { "Requisição de Energia" } 17 { "Erro de Baixa Potência" } default { "Indefinido" } }}}, @{Name="Bits por Pixel"; Expression={$_.CurrentBitsPerPixel}}, @{Name="Resolução Horizontal"; Expression={if ($_.CurrentHorizontalResolution) { "$($_.CurrentHorizontalResolution) px" } else { "Indefinido" }}}, @{Name="Resolução Vertical"; Expression={if ($_.CurrentVerticalResolution) { "$($_.CurrentVerticalResolution) px" } else { "Indefinido" }}}, @{Name="Número total de Cores"; Expression={if ($_.CurrentNumberOfColors) { $_.CurrentNumberOfColors } else { "Indefinido" }}}, @{Name="Taxa de Atualização Mínima (Hz)"; Expression={if ($_.MinRefreshRate) { $_.MinRefreshRate } else { "Indefinido" }}}, @{Name="Taxa de Atualização Máxima (Hz)"; Expression={if ($_.MaxRefreshRate) { $_.MaxRefreshRate } else { "Indefinido" }}}, @{Name="Modo de Scan"; Expression={ switch ($_.CurrentScanMode) { 3 { "Interlaced" } 4 { "Non-Interlaced" } default { "Indefinido" } }}}, @{Name="Tipo de Dither"; Expression={ switch ($_.DitherType) { 0 { "Nenhum" } 1 { "Padrão" } 2 { "Erro de Difusão" } 3 { "Grade de Dither" } 4 { "Grade Dinâmica" } 5 { "Matriz de Cores" } 6 { "Paleta de Cores" } 7 { "Difusão de Cores" } default { "Indefinido" } }}}, @{Name="Versão do Driver"; Expression={$_.DriverVersion}}, @{Name="Data do Driver"; Expression={if ($_.DriverDate) { [Management.ManagementDateTimeConverter]::ToDateTime($_.DriverDate).ToString("dd/MM/yyyy") } else { "Indefinido" }}}, @{Name="Seção de Informação"; Expression={$_.InfSection}}, @{Name="Arquivo de Informação"; Expression={$_.InfFileName}}, @{Name="Caminho para os Drivers"; Expression={if ($_.InstalledDisplayDrivers) { $_.InstalledDisplayDrivers -join ", " } else { "Indefinido" }}}, @{Name="Identificador PNP"; Expression={$_.PNPDeviceID}}, @{Name="Arquitetura de Vídeo"; Expression={ switch ($_.VideoArchitecture) { 1 { "Outro" } 2 { "Desconhecido" } 3 { "CGA" } 4 { "EGA" } 5 { "VGA" } 6 { "SVGA" } 7 { "MDA" } 8 { "HGC" } 9 { "MCGA" } 10 { "8514A" } 11 { "XGA" } 12 { "Linear Frame Buffer" } 160 { "PC-98" } default { "Indefinido" } }}}, @{Name="Tipo de Memória"; Expression={ switch ($_.VideoMemoryType) { 1 { "Outro" } 2 { "Desconhecido" } 3 { "VRAM" } 4 { "DRAM" } 5 { "SRAM" } 6 { "WRAM" } 7 { "EDO RAM" } 8 { "Burst Synchronous DRAM" } 9 { "Pipelined Burst SRAM" } 10 { "CDRAM" } 11 { "3DRAM" } 12 { "SDRAM" } 13 { "SGRAM" } default { "Indefinido" } }}}, @{Name="Status"; Expression={$_.Status}}; $videoControllers | Sort-Object ID