Write "Searching for Java files... ðŸŒ‹"; $result = (gci . -Recurse -File -Filter "*.java" | % { try { $oc = [Console]::ForegroundColor; [Console]::ForegroundColor = 'Gray'; [Console]::WriteLine("Found $($_.FullName)..."); [Console]::ForegroundColor = $oc; (cat $_.FullName -EA Stop).Count } catch { Write-Warning "Could not read file $($_.FullName)"; 0 } }) | measure -Sum; [PSCustomObject]@{'Total of Java Files' = $result.Count; 'Total of Java Lines' = $result.Sum}