Write "Searching for JavaScript-like files... üåê"; $result = (gci . -Recurse -File -Include *.js,*.cjs,*.mjs,*.jsx,*.ts,*.cts,*.mts,*.tsx,*.vue | % { try { $oc = [Console]::ForegroundColor; [Console]::ForegroundColor = 'Gray'; [Console]::WriteLine("Found $($_.FullName)..."); [Console]::ForegroundColor = $oc; (cat $_.FullName -EA Stop).Count } catch { Write-Warning "Could not read file $($_.FullName)"; 0 } }) | measure -Sum; [PSCustomObject]@{'Total of JavaScript (or derivate) Files' = $result.Count; 'Total of JavaScript (or derivate) Lines' = $result.Sum}