Write "Searching for Python... üêç"; $result = (gci . -Recurse -File -Filter "*.py" | % { try { $oc = [Console]::ForegroundColor; [Console]::ForegroundColor = 'Gray'; [Console]::WriteLine("Found $($_.FullName)..."); [Console]::ForegroundColor = $oc; (cat $_.FullName -EA Stop).Count } catch { Write-Warning "Could not read file $($_.FullName)"; 0 } }) | measure -Sum; [PSCustomObject]@{'Total of Python Files' = $result.Count; 'Total of Python Lines' = $result.Sum}